import React, { useEffect, useState } from "react";
import axios from "axios";
import ExpenseForm from "../components/ExpenseForm";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const Dashboard = () => {
    const [transactions, setTransactions] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [filterCategory, setFilterCategory] = useState(""); // Фильтр по категории
    const [sortType, setSortType] = useState("amount"); // Сортировка по умолчанию

    const fetchTransactions = async () => {
        try {
            const response = await axios.get(`${API_URL}/transactions`);
            setTransactions(response.data);
        } catch (error) {
            console.error("Ошибка загрузки транзакций:", error);
        }
    };

    const deleteTransaction = async (id) => {
        try {
            await axios.delete(`${API_URL}/transactions/${id}`);
            setRefresh(!refresh);
        } catch (error) {
            console.error("Ошибка удаления транзакции:", error);
        }
    };

    useEffect(() => {
        console.log("Список транзакций:", transactions);
      }, [transactions]);

    // Фильтруем по категории
    const filteredTransactions = transactions.filter((tx) =>
        filterCategory ? tx.category === filterCategory : true
    );

    // Сортируем по сумме или дате
    const sortedTransactions = [...filteredTransactions].sort((a, b) => {
        if (sortType === "amount") {
            return b.amount - a.amount;
        } else if (sortType === "date") {
            return new Date(b.date) - new Date(a.date);
        }
        return 0;
    });

    return (
        <div className="container">
            <h1>Dashboard</h1>
            <ExpenseForm onTransactionAdded={() => setRefresh(!refresh)} />

            {/* Фильтр по категории */}
            <div className="filter-sort">
                <select onChange={(e) => setFilterCategory(e.target.value)}>
                    <option value="">Все категории</option>
                    <option value="Food">Еда</option>
                    <option value="Transport">Транспорт</option>
                    <option value="Shopping">Покупки</option>
                </select>

                {/* Сортировка */}
                <select onChange={(e) => setSortType(e.target.value)}>
                    <option value="amount">По сумме</option>
                    <option value="date">По дате</option>
                </select>
            </div>

            <h2>Список транзакций</h2>
            <ul>
  {transactions.map((t, index) => (
    <li key={index}>{t.category}: {t.amount} {t.currency}</li>
  ))}
</ul>

        </div>
    );
};

export default Dashboard;
