import React, { useState } from "react";
import axios from "axios";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const ExpenseForm = ({ onTransactionAdded }) => {
    const [category, setCategory] = useState("Food");
    const [amount, setAmount] = useState("");
    const [currency, setCurrency] = useState("USD");

    const handleAddTransaction = () => {
        if (!amount || !category) return; // Проверка на пустые поля
        setTransactions([...transactions, { category, amount, currency }]);
      };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!amount) return;

        const newTransaction = { category, amount, currency, date: new Date().toISOString() };

        try {
            await axios.post(`${API_URL}/transactions`, newTransaction);
            onTransactionAdded(); // Обновить список после добавления
            setAmount(""); // Очищаем сумму после добавления
        } catch (error) {
            console.error("Ошибка добавления транзакции:", error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="expense-form">
            {/* Выпадающий список категорий */}
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="Food">Еда</option>
                <option value="Transport">Транспорт</option>
                <option value="Shopping">Покупки</option>
                <option value="Entertainment">Развлечения</option>
                <option value="Other">Другое</option>
            </select>

            {/* Поле ввода суммы */}
            <input
                type="number"
                placeholder="Сумма"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                required
            />

            {/* Выбор валюты */}
            <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="RUB">RUB</option>
            </select>

            <button onClick={handleAddTransaction}>Добавить</button>

        </form>
    );
};

export default ExpenseForm;
