import React, { useEffect, useState } from "react";
import axios from "axios";
import ExpenseForm from "../components/ExpenseForm";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const Dashboard = () => {
    const [transactions, setTransactions] = useState([]);
    const [refresh, setRefresh] = useState(false);

    const fetchTransactions = async () => {
        try {
            const response = await axios.get(`${API_URL}/transactions`);
            setTransactions(response.data);
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π:", error);
        }
    };

    const deleteTransaction = async (id) => {
        try {
            await axios.delete(`${API_URL}/transactions/${id}`);
            setRefresh(!refresh); // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏:", error);
        }
    };

    useEffect(() => {
        fetchTransactions();
    }, [refresh]);

    return (
        <div>
            <h1>Dashboard</h1>
            <ExpenseForm onTransactionAdded={() => setRefresh(!refresh)} />
            <h2>–°–ø–∏—Å–æ–∫ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π</h2>
            <ul>
                {transactions.map((tx) => (
                    <li key={tx._id}>
                        {tx.category}: {tx.amount} {tx.currency}
                        <button onClick={() => deleteTransaction(tx._id)}>üóë –£–¥–∞–ª–∏—Ç—å</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
