import React, { useState } from "react";
import axios from "axios";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const ExpenseForm = ({ onTransactionAdded }) => {
    const [category, setCategory] = useState("");
    const [amount, setAmount] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${API_URL}/transactions`, {
                category,
                amount: parseFloat(amount), // Приводим к числу
                date: new Date().toISOString(),
                currency: "USD" // Можно изменить
            });
            console.log("Транзакция добавлена:", response.data);
            onTransactionAdded(); // Обновляем список
        } catch (error) {
            console.error("Ошибка при добавлении транзакции:", error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                placeholder="Категория" 
                value={category} 
                onChange={(e) => setCategory(e.target.value)} 
                required 
            />
            <input 
                type="number" 
                placeholder="Сумма" 
                value={amount} 
                onChange={(e) => setAmount(e.target.value)} 
                required 
            />
            <button type="submit">Добавить</button>
        </form>
    );
};

export default ExpenseForm;
