import React, { useEffect, useState } from "react";
import axios from "axios";
import ExpenseForm from "../components/ExpenseForm";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const Dashboard = () => {
    const [transactions, setTransactions] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [filterCategory, setFilterCategory] = useState(""); // –§–∏–ª—å—Ç—Ä –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    const [sortType, setSortType] = useState("amount"); // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é

    const fetchTransactions = async () => {
        try {
            const response = await axios.get(`${API_URL}/transactions`);
            setTransactions(response.data);
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π:", error);
        }
    };

    const deleteTransaction = async (id) => {
        try {
            await axios.delete(`${API_URL}/transactions/${id}`);
            setRefresh(!refresh);
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏:", error);
        }
    };

    useEffect(() => {
        fetchTransactions();
    }, [refresh]);

    // –§–∏–ª—å—Ç—Ä—É–µ–º –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    const filteredTransactions = transactions.filter((tx) =>
        filterCategory ? tx.category === filterCategory : true
    );

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Å—É–º–º–µ –∏–ª–∏ –¥–∞—Ç–µ
    const sortedTransactions = [...filteredTransactions].sort((a, b) => {
        if (sortType === "amount") {
            return b.amount - a.amount;
        } else if (sortType === "date") {
            return new Date(b.date) - new Date(a.date);
        }
        return 0;
    });

    return (
        <div className="container">
            <h1>Dashboard</h1>
            <ExpenseForm onTransactionAdded={() => setRefresh(!refresh)} />

            {/* –§–∏–ª—å—Ç—Ä –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ */}
            <div className="filter-sort">
                <select onChange={(e) => setFilterCategory(e.target.value)}>
                    <option value="">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</option>
                    <option value="Food">–ï–¥–∞</option>
                    <option value="Transport">–¢—Ä–∞–Ω—Å–ø–æ—Ä—Ç</option>
                    <option value="Shopping">–ü–æ–∫—É–ø–∫–∏</option>
                </select>

                {/* –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ */}
                <select onChange={(e) => setSortType(e.target.value)}>
                    <option value="amount">–ü–æ —Å—É–º–º–µ</option>
                    <option value="date">–ü–æ –¥–∞—Ç–µ</option>
                </select>
            </div>

            <h2>–°–ø–∏—Å–æ–∫ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π</h2>
            <ul>
    {sortedTransactions.map((tx) => (
        <li key={tx._id} className="transaction-item">
            {tx.category}: <strong>{tx.amount} {tx.currency}</strong> ({new Date(tx.date).toLocaleDateString()})
            <button className="delete-btn" onClick={() => deleteTransaction(tx._id)}>
                üóë –£–¥–∞–ª–∏—Ç—å
            </button>
        </li>
    ))}
</ul>
        </div>
    );
};

export default Dashboard;
