import React, { useEffect, useState } from "react";
import { fetchTransactions, addTransaction } from "../services/api";
import ExpenseForm from "../components/ExpenseForm";

const Dashboard = () => {
    const [transactions, setTransactions] = useState([]);

    useEffect(() => {
        fetchTransactions().then(setTransactions);
    }, []);

    const handleAddTransaction = async (transaction) => {
        await addTransaction(transaction); // Отправляем данные на сервер
        const updatedTransactions = await fetchTransactions(); // Загружаем новые данные
        setTransactions(updatedTransactions); // Обновляем список
    };

    return (
        <div className="container">
            <h1>Dashboard</h1>
            <ExpenseForm onTransactionAdded={handleAddTransaction} />
            <h2>Список транзакций</h2>
            <ul>
                {transactions.map((t) => (
                    <li key={t.id}>{t.category}: {t.amount} {t.currency}</li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
