import React, { useState } from "react";
import axios from "axios";

const API_URL = "https://finance-tracker-api.onrender.com/api";

const ExpenseForm = ({ onTransactionAdded }) => {
    const [category, setCategory] = useState("");
    const [amount, setAmount] = useState("");
    const [currency, setCurrency] = useState("USD");

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTransaction = { category, amount, currency };

        try {
            await axios.post(`${API_URL}/transactions`, newTransaction);
            onTransactionAdded(); // Обновить список после добавления
            setCategory("");
            setAmount("");
        } catch (error) {
            console.error("Ошибка добавления транзакции:", error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Категория"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Сумма"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                required
            />
            <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="RUB">RUB</option>
            </select>
            <button type="submit">Добавить</button>
        </form>
    );
};

export default ExpenseForm;
